package com.yeterkarakus.miniyoutube.viewimport android.os.Bundleimport android.widget.Toastimport androidx.fragment.app.FragmentActivityimport com.yeterkarakus.miniyoutube.Rimport dagger.hilt.android.AndroidEntryPointimport javax.inject.Inject@AndroidEntryPointclass MainActivity : FragmentActivity() {    @Inject    lateinit var fragmentFactory: MiniYoutubeFragmentFactory    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        supportFragmentManager.fragmentFactory = fragmentFactory        setContentView(R.layout.activity_main)        if (intent.getBooleanExtra("isFromPush", false)) {            Toast.makeText(applicationContext, intent.getStringExtra("uuid"), Toast.LENGTH_LONG)                .show()            Toast.makeText(                applicationContext,                intent.getStringExtra("eventType"),                Toast.LENGTH_LONG            ).show()        } else {            Toast.makeText(applicationContext, "direkt açıldı", Toast.LENGTH_LONG).show()        }    }}